import React, { useState, useEffect, useContext } from "react";
import Navbar from "./Navbar";
import SpeedDialMenu from "./SpeedDialMenu";
import { useNavigate } from "react-router-dom";
import { MainContext } from "../context/MainContext";
import AuthService from "../services/auth.service";

import { Stack, Box } from "@mui/system";
import Paper from "@mui/material/Paper";
import { Typography } from "@mui/material";
import { LocalizationProvider } from "@mui/x-date-pickers";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { DateCalendar } from "@mui/x-date-pickers/DateCalendar";
import UserSearcherBar from "./UserSearcherBar";

function WorkoutTracker() {
  const [parsedData, setParsedData] = useState("");
  const [firstName, setfirstName] = useState("");
  const { jwt, setJwt } = useContext(MainContext);
  const [selectedDate, setSelectedDate] = useState(null);

  const navigate = useNavigate();

  // User information
  useEffect(() => {
    try {
      const decodedJwt = JSON.parse(atob(jwt.split(".")[1]));
      setParsedData(decodedJwt);
      setfirstName(decodedJwt.firstName);
    } catch (error) {
      AuthService.logout();
      setJwt("");
      return navigate("/");
    }
  }, [jwt, navigate, setJwt]);

  const handleDateSelection = (date) => {
    setSelectedDate(date);
  };

  return (
    <div>
      <Navbar />

      <Stack sx={{ display: "flex", alignItems: "center" }}>
        <Box
          sx={{
            mt: "10%",
            color: "white",
          }}
        >
          <Typography variant="h4" component="h2" sx={{ mb: "20%" }}>
            {firstName} , Select Day and Workout
          </Typography>
          <UserSearcherBar />
          <LocalizationProvider dateAdapter={AdapterDayjs}>
            <DateCalendar date={selectedDate} onChange={handleDateSelection} />
          </LocalizationProvider>
          <div className="speeddial">
            <SpeedDialMenu selectedDate={selectedDate} />
          </div>
        </Box>
      </Stack>

      <Stack sx={{ display: "flex", alignItems: "center" }}>
        <Box
          className="workoutplanner"
          sx={{
            width: 580,
            backgroundColor: "#6aa3bd",
            position: "relative",
            top: 32,
          }}
        >
          <Typography
            sx={{
              ml: 30,
            }}
          >
            Today's Training
          </Typography>
        </Box>
        <Box
          sx={{
            "& > :not(style)": {
              m: 1,
              width: 580,
              height: 450,
              backgroundColor: "#356c85",
            },
          }}
        >
          <Paper elevation={3} />
        </Box>
        <Box
          className="workoutplanner"
          sx={{
            bgcolor: "#6aa3bd",
            width: 580,
            position: "relative",
            bottom: 220,
          }}
        >
          <Typography
            sx={{
              ml: 28,
            }}
          >
            Last Time You Trained
          </Typography>
        </Box>
      </Stack>
    </div>
  );
}

export default WorkoutTracker;
