import React, { useState, useEffect, useContext } from "react";
import Navbar from "./Navbar";
import SpeedDialMenu from "./SpeedDialMenu";
import { useNavigate } from "react-router-dom";
import { MainContext } from "../context/MainContext";
import AuthService from "../services/auth.service";

import { Stack, Box } from "@mui/system";
import Paper from "@mui/material/Paper";
import { Typography } from "@mui/material";
import { LocalizationProvider } from "@mui/x-date-pickers";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { DateCalendar } from "@mui/x-date-pickers/DateCalendar";
import UserSearcherBar from "./UserSearcherBar";

function WorkoutTracker() {
  const [parsedData, setParsedData] = useState("");
  const [firstName, setfirstName] = useState("");
  const { jwt, setJwt } = useContext(MainContext);
  const navigate = useNavigate();

  useEffect(() => {
    try {
      const decodedJwt = JSON.parse(atob(jwt.split(".")[1]));
      setParsedData(decodedJwt);
      setfirstName(decodedJwt.firstName);
    } catch (error) {
      AuthService.logout();
      setJwt("");
      return navigate("/");
    }
  }, [jwt, navigate, setJwt]);

  return (
    <div>
      <Navbar />

      <Stack sx={{ display: "flex", alignItems: "center" }}>
        <Box
          sx={{
            mt: "10%",
            color: "white",
          }}
        >
          <Typography variant="h4" component="h2" sx={{ mb: "20%" }}>
            {firstName} , Select Day and Workout
          </Typography>
          <UserSearcherBar />
          <LocalizationProvider dateAdapter={AdapterDayjs}>
            <DateCalendar />
          </LocalizationProvider>
          <div className="speeddial">
            <SpeedDialMenu />
          </div>

          <Box
            sx={{
              display: "flex",
              flexWrap: "wrap",
              "& > :not(style)": {
                m: 1,
                width: 580,
                height: 50,
                backgroundColor: "#6aa3bd",
                position: "absolute",
              },
            }}
          >
            <Paper elevation={3} />
            <Typography>Today's Training</Typography>
          </Box>
          <Box
            sx={{
              display: "flex",
              flexWrap: "wrap",
              "& > :not(style)": {
                m: 1,
                width: 580,
                height: 450,
                backgroundColor: "#356c85",
              },
            }}
          >
            <Paper elevation={3} />
          </Box>
          <Box
            sx={{
              display: "flex",
              flexWrap: "wrap",
              "& > :not(style)": {
                m: 1,
                width: 580,
                height: 50,
                backgroundColor: "#6aa3bd",
                position: "absolute",
                top: "20px",
              },
            }}
          >
            <Paper elevation={3} />
            <Typography>Today's Training</Typography>
          </Box>
        </Box>
      </Stack>
    </div>
  );
}

export default WorkoutTracker;
