import React, { useState } from "react";
import Paper from "@mui/material/Paper";
import Box from "@mui/material/Box";
import { Stack } from "@mui/system";
import { Typography } from "@mui/material";
import { styled, alpha } from "@mui/material/styles";
import Button from "@mui/material/Button";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";

const StyledMenu = styled((props) => (
  <Menu
    elevation={0}
    anchorOrigin={{
      vertical: "bottom",
      horizontal: "right",
    }}
    transformOrigin={{
      vertical: "top",
      horizontal: "right",
    }}
    {...props}
  />
))(({ theme }) => ({
  "& .MuiPaper-root": {
    borderRadius: 6,
    marginTop: theme.spacing(1),
    minWidth: 180,
    color:
      theme.palette.mode === "light"
        ? "rgb(55, 65, 81)"
        : theme.palette.grey[300],
    boxShadow:
      "rgb(255, 255, 255) 0px 0px 0px 0px, rgba(0, 0, 0, 0.05) 0px 0px 0px 1px, rgba(0, 0, 0, 0.1) 0px 10px 15px -3px, rgba(0, 0, 0, 0.05) 0px 4px 6px -2px",
    "& .MuiMenu-list": {
      padding: "4px 0",
    },
    "& .MuiMenuItem-root": {
      "& .MuiSvgIcon-root": {
        fontSize: 18,
        color: theme.palette.text.secondary,
        marginRight: theme.spacing(1.5),
      },
      "&:active": {
        backgroundColor: alpha(
          theme.palette.primary.main,
          theme.palette.action.selectedOpacity
        ),
      },
    },
  },
}));

function WorkoutForm() {
  const [todoList, setTodoList] = useState([]);

  const handleAddTodo = (event) => {
    event.preventDefault();
    const todoInput = event.target.elements.todoInput.value;
    const todoCategory = event.target.elements.todoCategory.value;
    const todoTime = event.target.elements.todoTime.value;
    const todoReps = event.target.elements.todoReps.value;

    if (todoInput.trim() !== "") {
      setTodoList([
        ...todoList,
        {
          text: todoInput,
          category: todoCategory,
          time: todoTime,
          reps: todoReps,
          completed: false,
        },
      ]);
      event.target.elements.todoInput.value = "";
      event.target.elements.todoCategory.value = "";
      event.target.elements.todoTime.value = "";
      event.target.elements.todoReps.value = "";
    }
  };

  const handleToggleCompleted = (index) => {
    setTodoList(
      todoList.map((todo, i) =>
        i === index ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };

  const handleDeleteTodo = (index) => {
    setTodoList(todoList.filter((todo, i) => i !== index));
  };

  const handleEditTodo = (index, newText) => {
    setTodoList(
      todoList.map((todo, i) =>
        i === index ? { ...todo, text: newText } : todo
      )
    );
  };

  return (
    <Stack sx={{ display: "flex", alignItems: "center" }}>
      <Box
        sx={{
          display: "flex",
          flexWrap: "wrap",
          "& > :not(style)": {
            width: 370,
            height: 328,
          },
        }}
      >
        <Paper elevation={3} />
      </Box>
    </Stack>
  );
}

export default WorkoutForm;
