          <UserSearcherBar />
          <Calendar
            onChange={setDate}
            value={date}
            onClickDay={handleCalendarClick}
          />
          {showComponent ? (
            <div>
              {" "}
              <span>Default selected date:</span> {date.toDateString()}{" "}
            </div>
          ) : null}
        </Box>
      </Stack> */}
    </div>
  );
}

export default WorkoutTracker;
