import React, { useState, useEffect, useContext } from "react";
import Navbar from "./Navbar";

import { useNavigate } from "react-router-dom";
import { MainContext } from "../context/MainContext";
import AuthService from "../services/auth.service";
import UserSearcherBar from "./UserSearcherBar";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import WorkoutForm from "./WorkoutForm";
import ExerciseService from "../services/ExerciseService";

// Material UI
import { Stack, Box } from "@mui/system";
import { Typography } from "@mui/material";

function WorkoutTracker() {
  const [parsedData, setParsedData] = useState("");
  const [firstName, setfirstName] = useState("");
  const [date, setDate] = useState(new Date());
  const [showCode, setShowCode] = useState(false);
  const [showDefaultDate, setShowDefaultDate] = useState(false);
  const { jwt, setJwt } = useContext(MainContext);
  const [isPastDateSelected, setIsPastDateSelected] = useState(false);
  const [selectedDate, setSelectedDate] = useState(new Date());

  console.log(jwt);

  const navigate = useNavigate();

  // User information
  useEffect(() => {
    try {
      const decodedJwt = JSON.parse(atob(jwt.split(".")[1]));
      setParsedData(decodedJwt);
      setfirstName(decodedJwt.firstName);
    } catch (error) {
      AuthService.logout();
      setJwt("");
      return navigate("/");
    }
  }, [jwt, navigate, setJwt]);

  // Calendar Value
  const handleCalendarClick = (value) => {
    setDate(value);
    setShowCode(true);
    setShowDefaultDate(true);

    const currentDate = new Date();
    setIsPastDateSelected(value < currentDate);
  };

  const handleSaveWorkout = async (workoutData) => {
    try {
      const response = await ExerciseService.post("/workouts", workoutData);
      console.log(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const fetchWorkoutHistory = async () => {
    try {
      const response = await ExerciseService.get("/workouts");
      setExercises(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const saveWorkout = async (newExercise) => {
    await handleSaveWorkout(newExercise);
    await fetchWorkoutHistory();
  };

  return (
    <div>
      <Navbar />

      <Stack sx={{ display: "flex", alignItems: "center" }}>
        <Box
          sx={{
            mt: "10%",
            color: "white",
          }}
        >
          <Typography variant="h4" component="h2" sx={{ mb: "20%" }}>
            {firstName} , Select Day and Workout
          </Typography>
          <UserSearcherBar />
          <Calendar
            onChange={setDate}
            value={date}
            onClickDay={handleCalendarClick}
          />
          {showDefaultDate && (
            <div className="deafaultdate">
              <span style={{ marginLeft: "16%" }}>Default selected date:</span>{" "}
              {date.toDateString()}
            </div>
          )}
          {showCode && (
            <WorkoutForm
              onSaveWorkout={handleSaveWorkout}
              fetchWorkoutHistory={fetchWorkoutHistory}
              saveWorkout={saveWorkout}
              isPastDateSelected={isPastDateSelected}
              selectedDate={selectedDate} // pass selectedDate as prop
            />
          )}
        </Box>
      </Stack>
    </div>
  );
}

export default WorkoutTracker;
