import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import { Stack } from "@mui/system";

function WorkoutForm() {
  const [todoList, setTodoList] = useState([]);

  const handleAddTodo = (event) => {
    event.preventDefault();
    const todoInput = event.target.elements.todoInput.value;
    const todoCategory = event.target.elements.todoCategory.value;
    const todoTime = event.target.elements.todoTime.value;
    const todoReps = event.target.elements.todoReps.value;

    if (todoInput.trim() !== "") {
      setTodoList([
        ...todoList,
        {
          text: todoInput,
          category: todoCategory,
          time: todoTime,
          reps: todoReps,
          completed: false,
        },
      ]);
      event.target.elements.todoInput.value = "";
      event.target.elements.todoCategory.value = "";
      event.target.elements.todoTime.value = "";
      event.target.elements.todoReps.value = "";
    }
  };

  const handleToggleCompleted = (index) => {
    setTodoList(
      todoList.map((todo, i) =>
        i === index ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };

  const handleDeleteTodo = (index) => {
    setTodoList(todoList.filter((todo, i) => i !== index));
  };

  const handleEditTodo = (index, newText) => {
    setTodoList(
      todoList.map((todo, i) =>
        i === index ? { ...todo, text: newText } : todo
      )
    );
  };

  return (
    <Stack sx={{ display: "flex", alignItems: "center" }}>
      <Box
        sx={{
          "& > :not(style)": { m: 1, width: 300 },
        }}
        autoComplete="off"
        bgcolor="white"
      >
        <form onSubmit={handleAddTodo}>
          <TextField
            id="todoInput"
            name="todoInput"
            label="Enter Today's Workout"
            variant="filled"
            color="warning"
            focused
          />
          <TextField
            id="todoCategory"
            name="todoCategory"
            label="Category (Stretch, Strength, Cardio)"
            variant="filled"
            color="warning"
          />
          <TextField
            id="todoTime"
            name="todoTime"
            label="Time (mins)"
            variant="filled"
            color="warning"
          />
          <TextField
            id="todoReps"
            name="todoReps"
            label="Reps"
            variant="filled"
            color="warning"
          />
          <button type="submit">Add Todo</button>
        </form>
        <ul>
          {todoList.map((todo, index) => (
            <li key={index} className={todo.completed ? "completed" : ""}>
              <input
                type="checkbox"
                checked={todo.completed}
                onChange={() => handleToggleCompleted(index)}
              />
              <span>{todo.text}</span>
              <span>{todo.category}</span>
              <span>{todo.time} mins</span>
              <span>{todo.reps} reps</span>
              <button onClick={() => handleDeleteTodo(index)}>Delete</button>
              <button
                onClick={() => {
                  const newText = prompt("Enter new text", todo.text);
                  if (newText && newText.trim() !== "") {
                    handleEditTodo(index, newText);
                  }
                }}
              >
                Edit
              </button>
            </li>
          ))}
        </ul>
      </Box>
    </Stack>
  );
}

export default WorkoutForm;
