const authHeader = () => {
  try {
    const token = JSON.parse(localStorage.getItem("token"));
    if (token) {
      return {
        Authorization: "Bearer " + token,
      };
    }
  } catch (error) {
    console.error("Error parsing token:", error);
  }
  return {};
};

export default authHeader;
