import React, { useState, useEffect, useContext } from "react";
import Navbar from "./Navbar";

import { useNavigate } from "react-router-dom";
import { MainContext } from "../context/MainContext";
import AuthService from "../services/auth.service";
import UserSearcherBar from "./UserSearcherBar";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import WorkoutForm from "./WorkoutForm";

// Material UI
import { Stack, Box } from "@mui/system";
import { Typography, Paper } from "@mui/material";

function WorkoutTracker() {
  const [parsedData, setParsedData] = useState("");
  const [firstName, setfirstName] = useState("");
  const [date, setDate] = useState(new Date());
  const [isShown, setIsShown] = useState(false);
  const { jwt, setJwt } = useContext(MainContext);
  const navigate = useNavigate();

  // User information
  useEffect(() => {
    try {
      const decodedJwt = JSON.parse(atob(jwt.split(".")[1]));
      setParsedData(decodedJwt);
      setfirstName(decodedJwt.firstName);
    } catch (error) {
      AuthService.logout();
      setJwt("");
      return navigate("/");
    }
  }, [jwt, navigate, setJwt]);

  // Calendar Value
  const handleClick = (event) => {
    // üëáÔ∏è toggle shown state
    setIsShown((current) => !current);

    // üëáÔ∏è or simply set it to true
    setIsShown(true);
  };

  return (
    <div>
      <Navbar />

      <Stack sx={{ display: "flex", alignItems: "center" }}>
        <Box
          sx={{
            mt: "10%",
            color: "white",
          }}
        >
          <Typography variant="h4" component="h2" sx={{ mb: "20%" }}>
            {firstName} , Select Day and Workout
          </Typography>
          <UserSearcherBar />
          <Calendar onChange={setDate} value={date} />
          {isShown && <span className="bold">Default selected date:</span>}
        </Box>
      </Stack>
    </div>
  );
}

export default WorkoutTracker;
