import React, { useState } from "react";
import Paper from "@mui/material/Paper";
import Box from "@mui/material/Box";
import { Stack } from "@mui/system";
import { Select, MenuItem, TextField, Button } from "@mui/material";

function WorkoutForm() {
  const [workoutType, setWorkoutType] = useState("");
  const [workoutDetails, setWorkoutDetails] = useState("");
  const [customWorkoutType, setCustomWorkoutType] = useState("");
  const [workoutTypeOptions, setWorkoutTypeOptions] = useState([
    "Cardio",
    "Strength",
    "Stretch",
  ]);
  const [selectedWorkout, setSelectedWorkout] = useState(null);

  const handleWorkoutTypeChange = (event) => {
    setWorkoutType(event.target.value);
  };

  const handleWorkoutDetailsChange = (event) => {
    setWorkoutDetails(event.target.value);
  };

  const handleCustomWorkoutTypeChange = (event) => {
    setCustomWorkoutType(event.target.value);
  };

  const handleAddCustomWorkoutType = () => {
    if (
      customWorkoutType !== "" &&
      !workoutTypeOptions.includes(customWorkoutType)
    ) {
      setWorkoutTypeOptions([...workoutTypeOptions, customWorkoutType]);
      setCustomWorkoutType("");
    }
  };

  const handleAddWorkout = () => {
    setSelectedWorkout({
      type: workoutType,
      details: workoutDetails,
    });
    setWorkoutType("");
    setWorkoutDetails("");
  };

  return (
    <Stack sx={{ display: "flex", alignItems: "center" }}>
      <Box
        sx={{
          display: "flex",
          flexWrap: "wrap",
          "& > :not(style)": {
            width: 370,
            height: 328,
          },
        }}
      >
        <Select
          value={workoutType}
          onChange={handleWorkoutTypeChange}
          sx={{ minWidth: 120 }}
        >
          {workoutTypeOptions.map((option) => (
            <MenuItem key={option} value={option.toLowerCase()}>
              {option}
            </MenuItem>
          ))}
          {!workoutTypeOptions.includes("Other") && (
            <MenuItem value="other">Other</MenuItem>
          )}
        </Select>
        {workoutType === "other" && (
          <div style={{ display: "flex" }}>
            <TextField
              label="Exercise Type"
              value={customWorkoutType}
              onChange={handleCustomWorkoutTypeChange}
              sx={{ flexGrow: 1, mr: 1 }}
            />
            <Button onClick={handleAddCustomWorkoutType}>Add</Button>
          </div>
        )}
        {["cardio", "strength", "stretch"].includes(workoutType) && (
          <div style={{ display: "flex" }}>
            <TextField
              label={
                workoutType === "cardio"
                  ? "Distance (km)"
                  : workoutType === "strength"
                  ? "Weight (kg)"
                  : "Duration (minutes)"
              }
              value={workoutDetails}
              onChange={handleWorkoutDetailsChange}
              sx={{ flexGrow: 1, mr: 1 }}
            />
            <Button onClick={handleAddWorkout}>Add</Button>
          </div>
        )}
      </Box>
    </Stack>
  );
}

export default WorkoutForm;
